                        )}
                        <button type="button" onClick={handleReRecord} className="custom-button">
                          {language === 'hindi' ? 'पुनः रिकॉर्ड करें' : 'Re-record'}
                        </button>
                      </>
                    ) : (
                      <button
                        type="button"
                        onClick={handleMediaRecord}
                        style={{
                          marginBottom: '10px',
                          padding: '8px 12px',
                          cursor: 'pointer',
                          backgroundColor: '#007bff',
                          color: 'white',
                          border: 'none',
                          borderRadius: '4px',
                        }}
                      >
                        {isRecording
                          ? language === 'hindi'
                            ? 'रिकॉर्डिंग रोकें'
                            : 'Stop Recording'
                          : language === 'hindi'
                          ? selectedTab === 1
                            ? 'वीडियो रिकॉर्ड करें'
                            : 'आवाज़ रिकॉर्ड करें'
                          : selectedTab === 1
                          ? 'Record Video'
                          : 'Record Voice'}
                      </button>
                    )}
                  </div>

                  <button type="submit" disabled={!mediaURL} className="custom-button">
                    {language === 'hindi' ? 'जमा करें' : 'Submit'}
                  </button>

                  {showTick && <span className="tick">✔</span>}
                </form>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default FileUploadForm;
