                      <>
                        <div style={{ textAlign: 'center', marginTop: '10px' }}>
                          {isRecording && <p>Time remaining: {countdown}s</p>}
                        </div>
                        <button
                          type="button"
                          onClick={handleMediaRecord}
                          className="custom-button"
                          style={{
                            marginBottom: '10px',
                            padding: '8px 12px',
                            cursor: 'pointer',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                          }}
                        >
                          {isRecording
                            ? (language === 'hindi' ? 'रिकॉर्डिंग रोकें' : 'Stop Recording')
                            : language === 'hindi'
                            ? selectedTab === 1
                              ? 'वीडियो रिकॉर्ड करें'
                              : 'आवाज़ रिकॉर्ड करें'
                            : selectedTab === 1
                            ? 'Record Video'
                            : 'Record Voice'}
                        </button>
                      </>
                    )}
                  </div>

                  <button 
                    type="submit" 
                    disabled={!mediaURL || isSubmitting} 
                    className="custom-button"
                  >
                    {language === 'hindi' ? 'जमा करें' : 'Submit'}
                  </button>

                  {showTick && <span className="tick">✔</span>}
                </form>
              )}
            </div>
          </div>

          {/* Custom Refresh Warning Dialog */}
          {showRefreshWarning && (
            <div 
              className="modal-overlay"
              style={{
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0, 0, 0, 0.5)',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                zIndex: 9999
              }}
            >
              <div 
                className="modal-content"
                style={{
                  backgroundColor: 'white',
                  padding: '20px',
                  borderRadius: '8px',
                  maxWidth: '400px',
                  width: '90%'
                }}
              >
                <h2 style={{ marginBottom: '15px', fontSize: '1.2em', fontWeight: 'bold' }}>
                  {language === 'hindi' ? 'पृष्ठ रीफ्रेश की पुष्टि करें' : 'Confirm Page Refresh'}
                </h2>
                <p style={{ marginBottom: '20px', color: '#666' }}>
                  {language === 'hindi' 
                    ? 'पृष्ठ को रीफ्रेश करने से सभी वर्तमान प्रविष्टियां हट जाएंगी। क्या आप जारी रखना चाहते हैं?'
                    : 'Refreshing the page will remove all current entries. Do you want to continue?'}
                </p>
                <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                  <button
                    onClick={handleRefreshCancel}
                    className="custom-button"
                    style={{
                      padding: '8px 16px',
                      backgroundColor: '#6c757d',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    {language === 'hindi' ? 'रद्द करें' : 'Cancel'}
                  </button>
                  <button
                    onClick={handleRefreshConfirm}
                    className="custom-button"
                    style={{
                      padding: '8px 16px',
                      backgroundColor: '#007bff',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    {language === 'hindi' ? 'रीफ्रेश करें' : 'Refresh'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}
export default FileUploadForm;
