import React, { useState, useRef, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import tasks from './data/tasks';
import './App.css';

// Custom hook for media recording
const useMediaRecorder = (selectedTab) => {
  const [isRecording, setIsRecording] = useState(false);
  const [mediaURL, setMediaURL] = useState("");
  const [countdown, setCountdown] = useState(60);
  const mediaRecorderRef = useRef(null);
  const mediaChunksRef = useRef([]);

  const handleMediaRecord = async () => {
    const mediaType = selectedTab === 1 
      ? { video: true, audio: true }
      : { audio: true };

    if (!isRecording) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia(mediaType);
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorderRef.current = mediaRecorder;
        mediaChunksRef.current = [];

        mediaRecorder.start();
        setIsRecording(true);

        const countdownInterval = setInterval(() => {
          setCountdown(prev => {
            if (prev <= 1) {
              clearInterval(countdownInterval);
              if (mediaRecorder.state === "recording") {
                mediaRecorder.stop();
                stream.getTracks().forEach(track => track.stop());
                setIsRecording(false);
              }
              return 0;
            }
            return prev - 1;
          });
        }, 1000);

        mediaRecorder.countdownInterval = countdownInterval;

        mediaRecorder.addEventListener('dataavailable', (event) => {
          if (event.data.size > 0) {
            mediaChunksRef.current.push(event.data);
          }
        });

        mediaRecorder.addEventListener('stop', () => {
          const mediaBlob = new Blob(mediaChunksRef.current, {
            type: selectedTab === 1 ? 'video/webm' : 'audio/webm'
          });
          const url = URL.createObjectURL(mediaBlob);
          setMediaURL(url);
          setIsRecording(false);
          mediaChunksRef.current = [];
          clearInterval(mediaRecorder.countdownInterval);
          return mediaBlob;
        });
      } catch (error) {
        console.error('Error accessing media:', error);
        setIsRecording(false);
        alert(`Could not access your ${selectedTab === 1 ? 'camera' : 'microphone'}. Please check your browser settings.`);
      }
    } else {
      if (mediaRecorderRef.current) {
        mediaRecorderRef.current.stop();
        mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
        clearInterval(mediaRecorderRef.current.countdownInterval);
        setCountdown(0);
      }
    }
  };

  return {
    isRecording,
    mediaURL,
    countdown,
    handleMediaRecord,
    setMediaURL
  };
};

// Custom hook for handling submissions
const useSubmitHandler = (state, navigate, setShowTick, setCompletedTasks, selectedTab) => {
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e, mediaBlob, score = '0') => {
    e.preventDefault();
    if (isSubmitting) return;

    setIsSubmitting(true);

    try {
      if (!state?.patientId || !state?.name || !state?.age) {
        throw new Error('Patient information is missing');
      }

      const formData = new FormData();
      formData.append('name', state.name);
      formData.append('age', state.age);
      formData.append('patientId', state.patientId);
      formData.append('score', score);

      if (!mediaBlob && selectedTab !== 2) {
        throw new Error(`Please record ${selectedTab === 1 ? 'video' : 'audio'} before submitting.`);
      }

      if (selectedTab === 1) {
        formData.append('video', mediaBlob, 'video.webm');
      } else if (selectedTab === 0) {
        formData.append('audio', mediaBlob, 'audio.webm');
      }

      const response = await fetch('http://localhost:5001/save', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Upload failed');
      }

      setShowTick(true);
      setTimeout(() => {
        setShowTick(false);
        setCompletedTasks(prev => new Set([...prev, selectedTab]));
      }, 1000);

      return true;
    } catch (error) {
      console.error('Submission error:', error);
      alert(error.message || 'Error uploading file. Please try again.');
      return false;
    } finally {
      setIsSubmitting(false);
    }
  };

  return { isSubmitting, handleSubmit };
};

// Custom hook for session storage
const useSessionStorage = (key, initialValue) => {
  const [storedValue, setStoredValue] = useState(() => {
    try {
      const item = sessionStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error(error);
      return initialValue;
    }
  });

  useEffect(() => {
    try {
      sessionStorage.setItem(key, JSON.stringify(storedValue));
    } catch (error) {
      console.error(error);
    }
  }, [key, storedValue]);

  return [storedValue, setStoredValue];
};

// Utility functions
const calculateScore = (allSelectedAnswers) => {
  return Object.values(allSelectedAnswers).reduce(
    (total, selections) => total + (selections?.length || 0),
    0
  );
};

