// Main component
function FileUploadForm() {
  const location = useLocation();
  const navigate = useNavigate();
  const { state } = location;

  // State management with custom hooks
  const [selectedTab, setSelectedTab] = useState(0);
  const [language, setLanguage] = useState('english');
  const [showInitialPopup, setShowInitialPopup] = useState(true);
  const [showModal, setShowModal] = useState(true);
  const [showTick, setShowTick] = useState(false);
  const [completedTasks, setCompletedTasks] = useState(new Set());
  const [showImageModal, setShowImageModal] = useState(false);
  const [showInitialInstructions, setShowInitialInstructions] = useState(false);
  const [testCompleted, setTestCompleted] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  // Custom hooks
  const [allSelectedAnswers, setAllSelectedAnswers] = useSessionStorage('allSelectedAnswers', {});
  const { isRecording, mediaURL, countdown, handleMediaRecord, setMediaURL } = useMediaRecorder(selectedTab);
  const { isSubmitting, handleSubmit } = useSubmitHandler(
    state,
    navigate,
    setShowTick,
    setCompletedTasks,
    selectedTab
  );

  // Navigation protection
  useEffect(() => {
    if (!state?.patientId || !state?.name || !state?.age) {
      navigate('/');
    }
  }, [state, navigate]);

  // Handle tab switching
  const handleTabClick = (id) => {
    if (completedTasks.has(id) || id < selectedTab) return;

    setCountdown(60);
    setMediaURL("");
    setSelectedTab(id);
    setShowModal(id !== 2);
    setShowInitialInstructions(id === 2);
  };

  // Handle rerecording
  const handleReRecord = () => {
    setMediaURL("");
    handleMediaRecord();
  };

  // Render methods
  const renderMediaControls = () => {
    if (mediaURL) {
      return (
        <div className="media-playback">
          {selectedTab === 1 ? (
            <video src={mediaURL} controls style={{ maxWidth: '350px' }} />
          ) : (
            <audio src={mediaURL} controls />
          )}
          <button type="button" onClick={handleReRecord} className="custom-button">
            {language === 'hindi' ? '‡§™‡•Å‡§®‡§É ‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§° ‡§ï‡§∞‡•á‡§Ç' : 'Re-record'}
          </button>
        </div>
      );
    }

    return (
      <div className="recording-controls">
        {isRecording && <p>Time remaining: {countdown}s</p>}
        <button
          type="button"
          onClick={handleMediaRecord}
          className="custom-button"
        >
          {isRecording
            ? (language === 'hindi' ? '‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§°‡§ø‡§Ç‡§ó ‡§∞‡•ã‡§ï‡•á‡§Ç' : 'Stop Recording')
            : language === 'hindi'
            ? selectedTab === 1 ? '‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã ‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§° ‡§ï‡§∞‡•á‡§Ç' : '‡§Ü‡§µ‡§æ‡§ú‡§º ‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§° ‡§ï‡§∞‡•á‡§Ç'
            : selectedTab === 1 ? 'Record Video' : 'Record Voice'}
        </button>
      </div>
    );
  };

  const renderContent = () => {
    if (showInitialPopup) {
      return (
        <InitialPopup
          language={language}
          setLanguage={setLanguage}
          onClose={() => setShowInitialPopup(false)}
        />
      );
    }

    return (
      <>
        {showModal && <InstructionModal onClose={() => setShowModal(false)} language={language} />}
        <div className="container">
          {/* Tabs */}
          <div className="tabs">
            {tasks
              .filter((task) => !(task.id === 2 && testCompleted))
              .map((task) => (
                <div
                  key={task.id}
                  className={`tab ${selectedTab === task.id ? 'active' : ''} 
                            ${completedTasks.has(task.id) ? 'completed' : ''}
                            ${task.id < selectedTab ? 'disabled' : ''}`}
                  onClick={() => handleTabClick(task.id)}
                >
                  {task.title[language]}
                  {completedTasks.has(task.id) && <span>‚úì</span>}
                </div>
              ))}
          </div>

          {/* Content */}
          <div className="content">
            <TaskContent
              task={tasks[selectedTab]}
              language={language}
              testCompleted={testCompleted}
              showInitialInstructions={showInitialInstructions}
              setShowInitialInstructions={setShowInitialInstructions}
              setTestCompleted={setTestCompleted}
              allSelectedAnswers={allSelectedAnswers}
              setAllSelectedAnswers={setAllSelectedAnswers}
              currentQuestionIndex={currentQuestionIndex}
              setCurrentQuestionIndex={setCurrentQuestionIndex}
            />

            {/* Media Recording Form */}
            {selectedTab !== 2 && (
              <form onSubmit={(e) => handleSubmit(e, mediaURL)} className="media-form">
                <div className="media-controls">
                  {renderMediaControls()}
                </div>
                <button 
                  type="submit" 
                  disabled={!mediaURL || isSubmitting}
                  className="custom-button"
                >
                  {language === 'hindi' ? '‡§ú‡§Æ‡§æ ‡§ï‡§∞‡•á‡§Ç' : 'Submit'}
                </button>
                {showTick && <span className="tick">‚úî</span>}
              </form>
            )}
          </div>
        </div>
      </>
    );
  };

  return (
    <div className="app">
      {/* Patient ID Display */}
      {state?.patientId && (
        <div className="patient-id-display">
          Patient ID: {state.patientId}
        </div>
      )}
      {renderContent()}
    </div>
  );
}

// TaskContent component
const TaskContent = ({
  task,
  language,
  testCompleted,
  showInitialInstructions,
  setShowInitialInstructions,
  setTestCompleted,
  allSelectedAnswers,
  setAllSelectedAnswers,
  currentQuestionIndex,
  setCurrentQuestionIndex
}) => {
  if (task.id === 2) {
    if (testCompleted) {
      return <TaskCompletion language={language} allSelectedAnswers={allSelectedAnswers} />;
    }

    if (showInitialInstructions) {
      return (
        <InitialInstructions
          onAccept={() => setShowInitialInstructions(false)}
          onDeny={() => {
            setShowInitialInstructions(false);
            setTestCompleted(true);
          }}
          language={language}
        />
      );
    }

    return (
      <MultipleChoiceTask
        task={task}
        language={language}
        setTestCompleted={setTestCompleted}
        allSelectedAnswers={allSelectedAnswers}
        setAllSelectedAnswers={setAllSelectedAnswers}
        currentQuestionIndex={currentQuestionIndex}
        setCurrentQuestionIndex={setCurrentQuestionIndex}
      />
    );
  }

  return (
    <div className="task-content">
      {task.image ? (
        <div>
          <div className="zoom-instruction">
            {language === 'hindi' 
              ? '‡§ú‡§º‡•Ç‡§Æ ‡§á‡§®/‡§ú‡§º‡•Ç‡§Æ ‡§Ü‡§â‡§ü ‡§ï‡§∞‡§®‡•á ‡§ï‡•á ‡§≤‡§ø‡§è ‡§õ‡§µ‡§ø ‡§™‡§∞ ‡§ï‡•ç‡§≤‡§ø‡§ï ‡§ï‡§∞‡•á‡§Ç' 
              : 'Click on the image to zoom in / zoom out'} üîç
          </div>
          <img
            src={task.image}
            alt="Task"
            className="task-image"
            onClick={() => setShowImageModal(true)}
          />
        </div>
      ) : (
        <p className="task-paragraph">
          {task.paragraph[language]}
        </p>
      )}
    </div>
  );
};

// MultipleChoiceTask component
const MultipleChoiceTask = ({
  task,
  language,
  setTestCompleted,
  currentQuestionIndex,
  setCurrentQuestionIndex,
  allSelectedAnswers,
  setAllSelectedAnswers,
}) => {
  const handleAnswerSelect = (choiceIndex) => {
    setAllSelectedAnswers((prevSelected) => {
      const updatedAnswers = { ...prevSelected };
      if (!updatedAnswers[currentQuestionIndex]) {
        updatedAnswers[currentQuestionIndex] = [];
      }
      const currentAnswers = updatedAnswers[currentQuestionIndex];
      const index = currentAnswers.indexOf(choiceIndex);

      if (index === -1) {
        updatedAnswers[currentQuestionIndex] = [...currentAnswers, choiceIndex];
      } else {
        updatedAnswers[currentQuestionIndex] = currentAnswers.filter(
          (idx) => idx !== choiceIndex
        );
      }
      return updatedAnswers;
    });
  };

  const totalSelectedCount = Object.values(allSelectedAnswers).reduce(
    (total, selections) => total + (selections?.length || 0),
    0
  );

  const currentSelections = allSelectedAnswers[currentQuestionIndex] || [];

  return (
    <div className="multiple-choice-task">
      <h3>{task.questions[currentQuestionIndex].question[language]}</h3>
      <div className="choices-container">
        {task.questions[currentQuestionIndex].choices.map((choice, index) => (
          <label key={index} className="choice-label">
            <input
              type="checkbox"
              checked={currentSelections.includes(index)}
              onChange={() => handleAnswerSelect(index)}
            />
            {choice[language]}
          </label>
        ))}
      </div>
      <div className="navigation-container">
        <button 
          onClick={() => setCurrentQuestionIndex(prev => Math.max(0, prev - 1))}
          disabled={currentQuestionIndex === 0}
        >
          {language === 'hindi' ? '‡§™‡§ø‡§õ‡§≤‡§æ' : 'Previous'}
        </button>
        <button
          onClick={() => setCurrentQuestionIndex(prev => Math.min(task.questions.length - 1, prev + 1))}
          disabled={currentQuestionIndex === task.questions.length - 1}
        >
          {language === 'hindi' ? '‡§Ö‡§ó‡§≤‡§æ' : 'Next'}
        </button>
      </div>
      <div className="score-display">
        {language === 'hindi' ? `‡§ö‡§Ø‡§®‡§ø‡§§: ${totalSelectedCount}` : `Score: ${totalSelectedCount}`}
      </div>
    </div>
  );
};

// Export the component
export default FileUploadForm;
