import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import './App.css';

function FormPage() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const navigate = useNavigate();  // Used for navigation

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', name);
    formData.append('age', age);
    formData.append('video', document.getElementById('videoFile').files[0]);
    formData.append('audio', document.getElementById('audioFile').files[0]);

    const response = await fetch('http://localhost:8000/upload/', {
      method: 'POST',
      body: formData
    });

    const result = await response.json();
    console.log(result);

    // Navigate to the new page after submission
    navigate('/reading');  // Navigates to the ReadingPage
  };

  return (
    <div className="app">
      <div className="container">
        <h1>Welcome to My App</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Enter your age"
            value={age}
            onChange={(e) => setAge(e.target.value)}
            required
          />
          <input
            type="file"
            id="videoFile"
            accept="video/*"
            required
          />
          <input
            type="file"
            id="audioFile"
            accept="audio/*"
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

function ReadingPage() {
  const [selectedTile, setSelectedTile] = useState(1); // Track which tile is selected
  const [audio, setAudio] = useState(null);  // Track recorded audio

  // Function to handle audio recording (this will need integration with a recorder library)
  const handleRecord = () => {
    console.log("Recording started...");
    // Implement audio recording functionality here
  };

  const handleReRecord = () => {
    console.log("Re-recording...");
    // Implement re-recording functionality here
  };

  const handleSubmitAudio = () => {
    console.log("Audio submitted...");
    // Implement audio submission here
  };

  return (
    <div className="reading-page">
      <div className="sidebar">
        <div className={`tile ${selectedTile === 1 ? 'active' : ''}`} onClick={() => setSelectedTile(1)}>Tile 1</div>
        <div className={`tile ${selectedTile === 2 ? 'active' : ''}`} onClick={() => setSelectedTile(2)}>Tile 2</div>
        <div className={`tile ${selectedTile === 3 ? 'active' : ''}`} onClick={() => setSelectedTile(3)}>Tile 3</div>
        <div className={`tile ${selectedTile === 4 ? 'active' : ''}`} onClick={() => setSelectedTile(4)}>Tile 4</div>
      </div>
      <div className="content">
        <h2>Paragraph Reading Section</h2>
        <p>
          This is the paragraph the user will read out loud. Make sure to click on record when ready.
        </p>
        <button onClick={handleRecord}>Record</button>
        <button onClick={handleReRecord}>Re-record</button>
        <button onClick={handleSubmitAudio}>Submit</button>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<FormPage />} />
        <Route path="/reading" element={<ReadingPage />} />
      </Routes>
    </Router>
  );
}

export default App;
