// src/FileUploadForm.js
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

function FileUploadForm() {
  const { state } = useLocation(); // Corrected to use useLocation to get state
  const [video, setVideo] = useState(null);
  const [audio, setAudio] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', state.name);
    formData.append('age', state.age);
    formData.append('video', video);
    formData.append('audio', audio);

    const response = await fetch('http://localhost:8000/upload/', {
      method: 'POST',
      body: formData
    });

    const result = await response.json();
    console.log(result);
    alert(`Uploaded successfully: Video URL: ${result.video_url}, Audio URL: ${result.audio_url}`);
  };

  return (
    <div className="app">
      <div className="gradient-bg"></div>  {/* Gradient background */}
      <div className="container">
        <h1>Upload Video and Audio</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="file"
            id="videoFile"
            accept="video/*"
            onChange={(e) => setVideo(e.target.files[0])}
            required
          />
          <input
            type="file"
            id="audioFile"
            accept="audio/*"
            onChange={(e) => setAudio(e.target.files[0])}
            required
          />
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default FileUploadForm;
