import React from 'react';

function Tabs({ tasks, selectedTab, setSelectedTab, language }) {
  return (
    <div className="tabs">
      {tasks.map(task => (
        <div
          key={task.id}
          className={`tab ${selectedTab === task.id ? 'active' : ''}`}
          onClick={() => setSelectedTab(task.id)}
        >
          {task.title[language]}
        </div>
      ))}
    </div>
  );
}

export default Tabs;
