import React from 'react';

const TaskCompletion = ({ isPatient, allSelectedAnswers, language }) => {
  const calculateScore = () => Object.values(allSelectedAnswers).reduce((total, selections) => total + selections.length, 0);

  const score = calculateScore();
  const getCognitiveStatus = (score) => {
    if (score >= 24) return { english: 'Normal Cognition', hindi: 'सामान्य संज्ञानात्मक स्थिति' };
    if (score >= 19) return { english: 'Mild Cognitive Impairment', hindi: 'हल्की संज्ञानात्मक क्षति' };
    if (score >= 10) return { english: 'Moderate Cognitive Impairment', hindi: 'मध्यम संज्ञानात्मक क्षति' };
    return { english: 'Severe Cognitive Impairment', hindi: 'गंभीर संज्ञानात्मक क्षति' };
  };

  const status = getCognitiveStatus(score);

  return (
    <div className="task-completion">
      <h2>{language === 'hindi' ? 'परीक्षण परिणाम' : 'Test Results'}</h2>
      {isPatient ? (
        <p>{language === 'hindi' ? 'आपका परीक्षण पूरा हो गया है।' : 'Your test is complete.'}</p>
      ) : (
        <>
          <p>{language === 'hindi' ? `स्कोर: ${score}/30` : `Score: ${score}/30`}</p>
          <p>{language === 'hindi' ? `स्थिति: ${status.hindi}` : `Status: ${status.english}`}</p>
        </>
      )}
    </div>
  );
};

export default TaskCompletion;
