import React, { useState, useRef, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import tasks from './components/tasks';
import MMSETest from './components/MMSETest';
import './App.css';

function FileUploadForm() {
  const { state } = useLocation();
  const navigate = useNavigate();

  // Navigation and UI States
  const [selectedTab, setSelectedTab] = useState(0);
  const [showInitialPopup, setShowInitialPopup] = useState(true);
  const [showModal, setShowModal] = useState(true);
  const [showImageModal, setShowImageModal] = useState(false);
  const [showRefreshWarning, setShowRefreshWarning] = useState(false);
  const [completedTasks, setCompletedTasks] = useState(new Set());
  const [language, setLanguage] = useState('english');

  // Media Recording States
  const [isRecording, setIsRecording] = useState(false);
  const [mediaURL, setMediaURL] = useState("");
  const [audioBlob, setAudioBlob] = useState(null);
  const [videoBlob, setVideoBlob] = useState(null);
  const [countdown, setCountdown] = useState(60);
  const [showTick, setShowTick] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Refs
  const mediaRecorderRef = useRef(null);
  const mediaChunksRef = useRef([]);

  // Check for required patient info
  useEffect(() => {
    if (!state?.patientId || !state?.name || !state?.age) {
      navigate('/');
    }
  }, [state, navigate]);

  // Handle page refresh and navigation
  useEffect(() => {
    const handleBeforeUnload = (e) => {
      if (!showInitialPopup) {
        e.preventDefault();
        e.returnValue = '';
        return "Test is in progress. Refresh is disabled.";
      }
    };

    const handleKeyboardRefresh = (e) => {
      if (!showInitialPopup && (
        (e.metaKey && e.key === 'r') || 
        (e.ctrlKey && e.key === 'r') || 
        e.key === 'F5'
      )) {
        e.preventDefault();
        return false;
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    window.addEventListener('keydown', handleKeyboardRefresh);
    window.addEventListener('contextmenu', (e) => !showInitialPopup && e.preventDefault());

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
      window.removeEventListener('keydown', handleKeyboardRefresh);
    };
  }, [showInitialPopup]);

  const handleTabClick = (id) => {
    // Prevent navigation to completed or previous tasks
    if (completedTasks.has(id) || id < selectedTab) {
      return;
    }

    // Reset recording state
    setCountdown(60);
    setMediaURL("");
    mediaChunksRef.current = [];

    if (mediaRecorderRef.current?.state === 'recording') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
    }

    setSelectedTab(id);
    setShowModal(id !== 2);
  };

  const handleMediaRecord = () => {
    const mediaType = selectedTab === 1 
      ? { video: true, audio: true }
      : { audio: true };

    if (!isRecording) {
      // Start new recording
      setMediaURL("");
      setCountdown(60);
      setVideoBlob(null);
      setAudioBlob(null);

      navigator.mediaDevices.getUserMedia(mediaType)
        .then((stream) => {
          const mediaRecorder = new MediaRecorder(stream);
          mediaRecorderRef.current = mediaRecorder;
          mediaChunksRef.current = [];

          mediaRecorder.start();
          setIsRecording(true);

          const countdownInterval = setInterval(() => {
            setCountdown((prev) => {
              if (prev <= 1) {
                clearInterval(countdownInterval);
                if (mediaRecorder.state === "recording") {
                  mediaRecorder.stop();
                  stream.getTracks().forEach(track => track.stop());
                  setIsRecording(false);
                }
                return 0;
              }
              return prev - 1;
            });
          }, 1000);

          mediaRecorder.countdownInterval = countdownInterval;

          mediaRecorder.addEventListener('dataavailable', (event) => {
            if (event.data.size > 0) {
              mediaChunksRef.current.push(event.data);
            }
          });

          mediaRecorder.addEventListener('stop', () => {
            const mediaBlob = new Blob(mediaChunksRef.current, {
              type: selectedTab === 1 ? 'video/webm' : 'audio/webm'
            });
            const url = URL.createObjectURL(mediaBlob);
            setMediaURL(url);

            selectedTab === 1 ? setVideoBlob(mediaBlob) : setAudioBlob(mediaBlob);
            setIsRecording(false);
            mediaChunksRef.current = [];
            clearInterval(mediaRecorder.countdownInterval);
          });
        })
        .catch((err) => {
          console.error('Error accessing media:', err);
          setIsRecording(false);
          alert(`Could not access your ${selectedTab === 1 ? 'camera' : 'microphone'}. Please check your browser settings.`);
        });
    } else {
      // Stop current recording
      if (mediaRecorderRef.current) {
        mediaRecorderRef.current.stop();
        mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
        clearInterval(mediaRecorderRef.current.countdownInterval);
        setCountdown(0);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isSubmitting) return;

    setIsSubmitting(true);

    try {
      if (!state?.patientId || !state?.name || !state?.age) {
        throw new Error('Patient information is missing. Please start over.');
      }

      const formData = new FormData();
      formData.append('name', state.name);
      formData.append('age', state.age);
      formData.append('patientId', state.patientId);
      formData.append('score', '0'); // Only MMSE test has scores

      const currentBlob = selectedTab === 1 ? videoBlob : audioBlob;
      if (!currentBlob) {
        throw new Error(`Please record ${selectedTab === 1 ? 'video' : 'audio'} before submitting.`);
      }

      formData.append(selectedTab === 1 ? 'video' : 'audio', currentBlob, 
        selectedTab === 1 ? 'video.webm' : 'audio.webm');

      const response = await fetch('http://localhost:5001/save', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Upload failed');
      }

      setShowTick(true);
      setTimeout(() => {
        setShowTick(false);
        setCompletedTasks((prev) => new Set([...prev, selectedTab]));
        handleTabClick(selectedTab + 1);
      }, 1000);

      setMediaURL('');
      setVideoBlob(null);
      setAudioBlob(null);

    } catch (error) {
      console.error('Submission error:', error);
      alert(error.message || 'Error uploading file. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const renderTaskContent = () => {
    const currentTask = tasks[selectedTab];

    if (selectedTab === 2) {
      return (
        <MMSETest 
          language={language}
          onTestComplete={(score) => {
            setCompletedTasks(prev => new Set([...prev, 2]));
          }}
        />
      );
    }

    return (
      <>
        <div className="task-content">
          {currentTask.image ? (
            <>
              <div className="text-sm text-gray-600 mb-2 cursor-pointer">
                {language === 'hindi' 
                  ? '‡§ú‡§º‡•Ç‡§Æ ‡§á‡§®/‡§ú‡§º‡•Ç‡§Æ ‡§Ü‡§â‡§ü ‡§ï‡§∞‡§®‡•á ‡§ï‡•á ‡§≤‡§ø‡§è ‡§õ‡§µ‡§ø ‡§™‡§∞ ‡§ï‡•ç‡§≤‡§ø‡§ï ‡§ï‡§∞‡•á‡§Ç' 
                  : 'Click on the image to zoom in / zoom out'} üîç
              </div>
              <img
                src={currentTask.image}
                alt="Task"
                className="cursor-pointer max-w-full max-h-full"
                onClick={() => setShowImageModal(true)}
              />
              {showImageModal && (
                <div className="image-modal" onClick={() => setShowImageModal(false)}>
                  <img
                    src={currentTask.image}
                    alt="Task Full Size"
                    className="max-w-90 max-h-90 object-contain"
                  />
                </div>
              )}
            </>
          ) : (
            <p className="w-3/5 mx-auto leading-relaxed text-lg">
              {currentTask.paragraph[language]}
            </p>
          )}
        </div>

        <form onSubmit={handleSubmit} className="media-form flex flex-col items-center gap-4 mt-5">
          <div className="media-controls">
            {mediaURL ? (
              <div className="media-playback">
                {selectedTab === 1 ? (
                  <video 
                    src={mediaURL} 
                    controls
                    className="max-w-sm w-full h-auto mb-3"
                  />
                ) : (
                  <audio src={mediaURL} controls />
                )}
                <button 
                  type="button" 
                  onClick={() => handleMediaRecord()}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  {language === 'hindi' ? '‡§™‡•Å‡§®‡§É ‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§° ‡§ï‡§∞‡•á‡§Ç' : 'Re-record'}
                </button>
              </div>
            ) : (
              <div className="recording-controls">
                {isRecording && (
                  <p className="text-center mb-2">Time remaining: {countdown}s</p>
                )}
                <button
                  type="button"
                  onClick={handleMediaRecord}
                  className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                  {isRecording
                    ? (language === 'hindi' ? '‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§°‡§ø‡§Ç‡§ó ‡§∞‡•ã‡§ï‡•á‡§Ç' : 'Stop Recording')
                    : language === 'hindi'
                    ? selectedTab === 1
                      ? '‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã ‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§° ‡§ï‡§∞‡•á‡§Ç'
                      : '‡§Ü‡§µ‡§æ‡§ú‡§º ‡§∞‡§ø‡§ï‡•â‡§∞‡•ç‡§° ‡§ï‡§∞‡•á‡§Ç'
                    : selectedTab === 1
                    ? 'Record Video'
                    : 'Record Voice'}
                </button>
              </div>
            )}
          </div>

          <button 
            type="submit" 
            disabled={!mediaURL || isSubmitting}
            className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700 
                     disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {language === 'hindi' ? '‡§ú‡§Æ‡§æ ‡§ï‡§∞‡•á‡§Ç' : 'Submit'}
          </button>

          {showTick && <span className="text-green-600 text-2xl">‚úî</span>}
        </form>
      </>
    );
  };

  return (
    <div className="app">
      {/* Patient ID Display */}
      {state?.patientId && (
        <div className="fixed top-5 right-5 bg-white px-3 py-2 rounded shadow-sm 
                      border border-gray-200 font-mono text-sm">
          Patient ID: {state.patientId}
        </div>
      )}

      {showInitialPopup ? (
        <InitialPopup
          language={language}
          setLanguage={setLanguage}
          onClose={() => setShowInitialPopup(false)}
        />
      ) : (
        <>
          {showModal && selectedTab !== 2 && (
            <InstructionModal 
              language={language} 
              selectedTab={selectedTab}
              onClose={() => setShowModal(false)} 
            />
          )}

          <div className="gradient-bg" />
          <div className="container">
            {/* Navigation Tabs */}
            <div className="tabs">
              {tasks.map((task) => (
                <div
                  key={task.id}
                  onClick={() => handleTabClick(task.id)}
                  className={`tab ${selectedTab === task.id ? 'active' : ''} 
                            ${completedTasks.has(task.id) ? 'completed' : ''}
                            ${task.id < selectedTab ? 'disabled' : ''}`}
                >
                  {task.title[language]}
                  {completedTasks.has(task.id) && (
                    <span className="ml-1 text-green-600">‚úì</span>
                  )}
                </div>
              ))}
            </div>

            {/* Main Content Area */}
            <div className="content">
              {renderTaskContent()}
            </div>
          </div>

          {/* Refresh Warning Modal */}
          {showRefreshWarning && (
            <RefreshWarningModal 
              language={language}
              onConfirm={() => {
                setShowRefreshWarning(false);
                window.location.reload();
              }}
              onCancel={() => setShowRefreshWarning(false)}
            />
          )}
        </>
      )}
    </div>
  );
}

export default FileUploadForm;
