import React from 'react';

function LanguageToggleButton({ language, toggleLanguage }) {
  return (
    <button
      onClick={toggleLanguage}
      style={{
        marginBottom: '10px',
        padding: '8px 12px',
        cursor: 'pointer',
        backgroundColor: '#007bff',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
      }}
    >
      {language === 'hindi' ? 'Switch to English' : 'हिंदी पर स्विच करें'}
    </button>
  );
}

export default LanguageToggleButton;
